import React, { useState } from "react";

const categories = ["Fertilizers", "Crop Seeds", "Pesticides", "Crops"];

const products = [
  {
    name: "Urea Fertilizer",
    category: "Fertilizers",
    price: 500,
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Wheat Seeds",
    category: "Crop Seeds",
    price: 300,
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Neem Pesticide",
    category: "Pesticides",
    price: 200,
    image: "data:image/jpeg;base64,",
  },
  {
    name: "Fresh Tomatoes",
    category: "Crops",
    price: 150,
    image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTGrlSoNcyDnMIqd1dfFkeeHLYESBofcq4I7xCHoFd1z2FyTjN3VtpB0ECD20gtLzYF3yYSyosJ8hfUC6wQ9kg6iRMGlJE1YFGwdC9jGDcztw",
  },
];

export default function AgriBazaar() {
  const [selectedCategory, setSelectedCategory] = useState("All");
  const [cart, setCart] = useState([]);

  const filteredProducts =
    selectedCategory === "All"
      ? products
      : products.filter((p) => p.category === selectedCategory);

  const addToCart = (product) => {
    const existing = cart.find((item) => item.name === product.name);
    if (existing) {
      setCart(
        cart.map((item) =>
          item.name === product.name
            ? { ...item, quantity: item.quantity + 1 }
            : item
        )
      );
    } else {
      setCart([...cart, { ...product, quantity: 1 }]);
    }
  };

  const handlePlaceOrder = () => {
    alert("üéâ Your order has been placed ‚úÖ");
    setCart([]);
  };

  return (
    <div className="max-w-6xl mx-auto px-4 py-8 mt-10">
      <h1 className="text-3xl font-bold mb-6 text-green-800">üåæ AgriBazaar</h1>

      {/* Category Filter */}
      <div className="flex gap-4 mb-6 flex-wrap">
        <button
          onClick={() => setSelectedCategory("All")}
          className={`px-4 py-2 rounded-full border ${
            selectedCategory === "All"
              ? "bg-green-700 text-white"
              : "bg-white text-green-700 border-green-700"
          }`}
        >
          All
        </button>
        {categories.map((cat) => (
          <button
            key={cat}
            onClick={() => setSelectedCategory(cat)}
            className={`px-4 py-2 rounded-full border ${
              selectedCategory === cat
                ? "bg-green-700 text-white"
                : "bg-white text-green-700 border-green-700"
            }`}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Product Grid */}
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
        {filteredProducts.map((product, index) => (
          <div
            key={index}
            className="border rounded-lg p-4 shadow-md bg-white flex flex-col items-center"
          >
            <img
              src={product.image}
              alt={product.name}
              className="w-full h-40 object-cover mb-4 rounded"
            />
            <h2 className="text-lg font-bold mb-2">{product.name}</h2>
          
            <p className="text-green-700 font-semibold mb-2">
              ‚Çπ{product.price}
            </p>
            <button
              onClick={() => addToCart(product)}
              className="mt-auto bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded"
            >
              Add to Cart
            </button>
            
          </div>
        ))}
      </div>

      {/* No products found */}
      {filteredProducts.length === 0 && (
        <p className="text-gray-500 mt-10 text-center">
          ‚ùå No matching products found.
        </p>
      )}

      {/* üõí Cart Section */}
      {cart.length > 0 && (
        <div className="mt-16 border-t pt-8">
          <h2 className="text-2xl font-bold text-green-800 mb-4">
            üõçÔ∏è Your Cart
          </h2>
          <table className="w-full table-auto border mb-6">
            <thead>
              <tr className="bg-green-100 text-left">
                <th className="p-2 border">Item</th>
                <th className="p-2 border">Price</th>
                <th className="p-2 border">Quantity</th>
                <th className="p-2 border">Subtotal</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr key={index} className="border-t">
                  <td className="p-2 border">{item.name}</td>
                  <td className="p-2 border">‚Çπ{item.price}</td>
                  <td className="p-2 border">{item.quantity}</td>
                  <td className="p-2 border">
                    ‚Çπ{item.price * item.quantity}
                  </td>
                </tr>
              ))}
            </tbody>
            <tfoot>
              <tr className="font-semibold bg-green-50">
                <td colSpan="3" className="p-2 border text-right">
                  Total:
                </td>
                <td className="p-2 border">
                  ‚Çπ
                  {cart.reduce(
                    (sum, item) => sum + item.price * item.quantity,
                    0
                  )}
                </td>
              </tr>
            </tfoot>
          </table>

          {/* Place Order Button */}
          <button
            onClick={handlePlaceOrder}
            className="bg-green-600 hover:bg-green-700 text-white px-6 py-3 rounded text-lg"
          >
            üßæ Place Order
          </button>
        </div>
      )}
    </div>
  );
}
